mapper.select:
SELECT

FROM qms_user_menu t LEFT JOIN  qms_menu t1 ON t.menu_id = t1.id
where t.user_id = #{userId,jdbcType=VARCHAR}

  and t1.type = #{type,jdbcType=VARCHAR}

ORDER BY  t1.menu_order

SELECT

FROM qms_role_menu t LEFT JOIN  qms_menu t1 ON t.menu_id = t1.id
where t.role_id = #{roleId,jdbcType=VARCHAR}

  and t1.type = #{type,jdbcType=VARCHAR}

ORDER BY  t1.menu_order
